import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/common/Header";

const MainPage = () => {
  const navigate = useNavigate();
  // 호버 상태를 관리할 state 추가
  const [hoveredMenu, setHoveredMenu] = useState(null);

  // 메뉴 클릭 핸들러
  const handleMenuClick = (menuName) => {
    console.log(`${menuName} 메뉴가 클릭되었습니다`);
    // 페이지 이동 로직
    switch (menuName) {
      case "분석하기":
        navigate("/analyze");
        break;
      case "분석내역":
        navigate("/history");
        break;
      case "유지관리":
        navigate("/maintenance");
        break;
      case "근처 세탁소":
        navigate("/laundry-map");
        break;
      default:
        break;
    }
  };

  // 메뉴 항목 렌더링 함수
  const renderMenuItem = (menuName, description) => {
    const isHovered = hoveredMenu === menuName;

    return (
      <div
        className={`flex flex-col items-center justify-center rounded-full p-4 md:p-6 text-center aspect-square relative cursor-pointer transition-all duration-300 overflow-hidden`}
        onClick={() => handleMenuClick(menuName)}
        onMouseEnter={() => setHoveredMenu(menuName)}
        onMouseLeave={() => setHoveredMenu(null)}
        style={{
          backgroundColor: "#B7F3FF",
          transform: isHovered ? "scale(1.05)" : "scale(1)",
        }}
      >
        {isHovered && (
          <>
            {[...Array(8)].map((_, i) => (
              <div
                key={i}
                className="absolute rounded-full bg-sky-100 opacity-70"
                style={{
                  width: `${Math.random() * 30 + 10}px`,
                  height: `${Math.random() * 30 + 10}px`,
                  left: `${Math.random() * 100}%`,
                  top: `${Math.random() * 100}%`,
                  animation: `bubbleBurst 0.8s ease-out forwards`,
                  animationDelay: `${i * 0.05}s`,
                }}
              />
            ))}
          </>
        )}
        <p className="m-0 font-bold text-base md:text-lg z-10">{menuName}</p>
        <span className="text-xs md:text-sm text-gray-700 z-10 mt-1 px-1 leading-tight">
          {description}
        </span>
      </div>
    );
  };

  return (
    <div
      className="flex flex-col min-h-screen w-full bg-gray-50 overflow-y-auto"
      style={{ fontFamily: "'Sandol', 'YoonSSH', sans-serif" }}
    >
      <Header />

      {/* 메인 콘텐츠 */}
      <div className="flex flex-col items-center flex-none">
        <div className="w-full max-w-6xl mx-auto relative">
          <div className="w-full">
            <img
              src={require("../assets/images/mainbanner.png")}
              alt="얼룩 옷을 들고 있는 여성"
              className="w-full h-auto block"
            />
          </div>
        </div>
      </div>

      {/* 하단 메뉴 */}
      <div className="container mx-auto max-w-6xl px-4 py-6">
        <div className="grid grid-cols-2 md:grid-cols-4 gap-3 sm:gap-4 md:gap-6">
          {renderMenuItem("분석하기", "사진을 찍어 얼룩의 유형을 분석해보세요")}
          {renderMenuItem("분석내역", "이전에 분석한 내용을 다시 확인하세요")}
          {renderMenuItem(
            "유지관리",
            "의류에는 환경과 성분보존을 위한 방법이 있어요"
          )}
          {renderMenuItem(
            "근처 세탁소",
            "가장 가까운 위치의 세탁소를 찾아보세요"
          )}
        </div>
      </div>

      {/* CSS 애니메이션 스타일 */}
      <style jsx>{`
        @keyframes bubbleBurst {
          0% {
            transform: scale(0);
            opacity: 0.7;
          }
          50% {
            opacity: 0.5;
          }
          100% {
            transform: scale(3);
            opacity: 0;
          }
        }
      `}</style>
    </div>
  );
};

export default MainPage;
